import React, { useState, useEffect, useRef } from "react";

function App() {
  const pi =
    "3.    ";
  const canvasRef = useRef(null);
  let _x = 0;
  let _y = 0;
  let piIndex = 0;
  const [active, setActive] = useState(false);
  let isTouch = false;

  function MouseMoveHandler(e) {
    console.log(e);
    if (isTouch && !e.touches) return;

    let x = e.clientX;
    let y = e.clientY;
    if (e.touches) {
      isTouch = true;
      x = e.touches[0].clientX;
      y = e.touches[0].clientY;
    }

    console.log(x, y, active);
    if (!active) return;

    if (Math.pow(Math.abs(x - _x), 2) + Math.pow(Math.abs(y - _y), 2) > 500) {
      _x = x;
      _y = y;
      const canvas = canvasRef.current;
      var ctx = canvas.getContext("2d");
      // write pi digit
      ctx.font = "16px Inter";
      ctx.fillStyle = "black";
      ctx.textAlign = "center";
      ctx.fillText(pi[piIndex], x, y);
      piIndex = (piIndex + 1) % pi.length;
    }
  }

  useEffect(() => {
    const canvas = canvasRef.current;

    function resizeCanvas() {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    }
    resizeCanvas();
    window.addEventListener("resize", resizeCanvas, false);
  }, []);

  function handleClick() {
    console.log(!active);
    setActive(!active);
    if (!active) {
      const canvas = canvasRef.current;
      var ctx = canvas.getContext("2d");
      ctx.save();
      ctx.globalAlpha = 1;
      ctx.globalCompositeOperation = "destination-in";
      ctx.fillStyle = "rgba(0, 0, 0, 0.1)";
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      ctx.restore();
    }
  }

  return (
    <div
      className="absolute w-screen h-screen"
      onMouseMove={MouseMoveHandler}
      onTouchMove={MouseMoveHandler}
      onClick={handleClick}
    >
      <div
        className={
          "relative flex min-h-screen items-center justify-center" +
          (active ? " z-0" : " z-20")
        }
      >
        <div
          className={
            "text-5xl p-4 rounded-lg cursor-pointer text-black" +
            (active
              ? " bg-gray-100 opacity-20 text-opacity-20"
              : " shadow-2xl bg-white opacity-100 text-opacity-100")
          }
          style={{
            transitionTimingFunction: "ease-in-out",
            transitionDuration: "1s",
            transitionProperty: "all",
          }}
        >
          Cursor Pi
        </div>
      </div>
      <div className="absolute inset-0 z-10 pointer-events-none">
        <canvas ref={canvasRef} className="w-full h-full block">
          Your browser does not support the HTML canvas tag.
        </canvas>
      </div>
    </div>
  );
}

export default App;
